//program to demonstrate ternary operator.

package com.capgemini.corejavademo;

public class TernaryOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 10;
		
		//ternary operator
		x = (10 == x) ? 1 : 0;
		
		System.out.println(x);

	}

}
